#!/bin/bash
function signal_cf() {
  local status=$1
  local message=$2
  echo "Signaling with status: $status, message: $message"
  curl -X PUT -H 'Content-Type:' --data-binary '{"Status":"'$status'","Reason":"'$message'","UniqueId":"'$status'","Data":"'$message'"}' "

