{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "rm -rf /tmp/workspace/*",
        "mkdir -p /tmp/workspace",
        "cp -r . /tmp/workspace/optimus-core",
        "cd /tmp/workspace",
        "git clone https://github.com/mzienert/sentinel.git || (cd sentinel && git pull)"
      ]
    },
    "pre_build": {
      "commands": [
        "cd /tmp/workspace/sentinel/lambdas/hello-world",
        "rm -rf node_modules dist",
        "npm ci",
        "npm run build",
        "cp -r node_modules dist/",
        "cp package*.json dist/",
        "cd /tmp/workspace/sentinel"
      ]
    },
    "build": {
      "commands": [
        "rm -rf node_modules dist",
        "npm ci",
        "npm run build",
        "mkdir -p dist/node_modules",
        "cp -r node_modules/* dist/node_modules/",
        "cp package*.json dist/"
      ]
    },
    "post_build": {
      "commands": [
        "mkdir -p dist/logs",
        "chmod -R 755 dist"
      ]
    }
  },
  "artifacts": {
    "base-directory": "/tmp/workspace/sentinel",
    "files": [
      "appspec.yml",
      "scripts/**/*",
      "ecosystem.config.js",
      "package*.json",
      "dist/**/*",
      "lambdas/hello-world/dist/**/*"
    ]
  }
}

{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "n 20",
        "npm ci --force"
      ]
    },
    "build": {
      "commands": [
        "echo \"Building Next.js application...\"",
        "npm run build",
        "ls -la",
        "ls -la .next/"
      ]
    },
    "post_build": {
      "commands": [
        "echo \"Setting cache control headers...\"",
        "cd .next",
        "aws s3 sync . s3://${WEBSITE_BUCKET} --delete --cache-control \"public, max-age=31536000, immutable\"",
        "cd ../public",
        "aws s3 sync . s3://${WEBSITE_BUCKET} --cache-control \"public, max-age=31536000, immutable\"",
        "echo \"Creating CloudFront invalidation...\"",
        "aws cloudfront create-invalidation --distribution-id ${CLOUDFRONT_DISTRIBUTION_ID} --paths \"/*\""
      ]
    }
  },
  "artifacts": {
    "base-directory": "out",
    "files": [
      "**/*"
    ]
  },
  "cache": {
    "paths": [
      "node_modules/**/*"
    ]
  }
}
